// src/pages/UploadMaterial.jsx
import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from "axios";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import CourseCard from "../components/CourseCard";
import DocumentCard from "../components/DocumentCard";

const API = "http://localhost:3001";

function UploadMaterial() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { register, handleSubmit, reset } = useForm();
  const [materials, setMaterials] = useState([]);

  useEffect(() => {
    if (!user || user.role !== "teacher") {
      navigate("/login");
    } else {
      axios.get(`${API}/materials`).then((res) => setMaterials(res.data));
    }
  }, [user, navigate]);

  const onSubmit = async (data) => {
    const newDoc = { ...data, id: Date.now() };
    await axios.post(`${API}/materials`, newDoc);
    setMaterials([...materials, newDoc]);
    reset();
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Tải lên tài liệu</h2>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-2 mb-4">
        <input
          {...register("title")}
          placeholder="Tiêu đề"
          className="border p-2 w-full"
          required
        />
        <input
          {...register("description")}
          placeholder="Mô tả"
          className="border p-2 w-full"
        />
        <input
          {...register("link")}
          placeholder="Liên kết (PDF, video...)"
          className="border p-2 w-full"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2">
          Tải lên
        </button>
      </form>

      <ul className="space-y-2">
        {materials.map((doc) => (
          <li key={doc.id} className="border p-3 rounded">
            <strong>{doc.title}</strong> - {doc.description}
            {doc.link && (
              <div>
                <a
                  href={doc.link}
                  target="_blank"
                  rel="noreferrer"
                  className="text-blue-500 underline"
                >
                  Xem tài liệu
                </a>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UploadMaterial;
