// src/pages/CourseManage.jsx
import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from "axios";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import CourseCard from "../components/CourseCard";
import DocumentCard from "../components/DocumentCard";

const API = "http://localhost:3001";

function CourseManage() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const { register, handleSubmit, reset } = useForm();

  useEffect(() => {
    if (!user || user.role !== "teacher") {
      navigate("/login");
    } else {
      axios.get(`${API}/courses`).then((res) => setCourses(res.data));
    }
  }, [user, navigate]);

  const onSubmit = async (data) => {
    const newCourse = { ...data, id: Date.now() };
    await axios.post(`${API}/courses`, newCourse);
    setCourses([...courses, newCourse]);
    reset();
  };

  const handleDelete = async (id) => {
    await axios.delete(`${API}/courses/${id}`);
    setCourses(courses.filter((c) => c.id !== id));
  };

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Quản lý môn học</h2>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-2 mb-6">
        <input
          {...register("code")}
          placeholder="Mã môn"
          className="border p-2 w-full"
          required
        />
        <input
          {...register("name")}
          placeholder="Tên môn học"
          className="border p-2 w-full"
          required
        />
        <button type="submit" className="bg-green-500 text-white px-4 py-2">
          Thêm môn
        </button>
      </form>

      <ul className="space-y-2">
        {courses.map((course) => (
          <li
            key={course.id}
            className="flex justify-between items-center border p-3 rounded"
          >
            <div>
              <strong>{course.code}</strong>: {course.name}
            </div>
            <button
              onClick={() => handleDelete(course.id)}
              className="bg-red-500 text-white px-2 py-1"
            >
              Xoá
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CourseManage;
