// src/components/Sidebar.jsx
import React from "react";
import { Link, useLocation } from "react-router-dom";
import { Menu, Layout } from "antd";
import {
  HomeOutlined,
  BookOutlined,
  FileTextOutlined,
  UploadOutlined,
  SettingOutlined,
  TeamOutlined,
} from "@ant-design/icons";
import { useAuth } from "../context/AuthContext";

const { Sider } = Layout;

const Sidebar = () => {
  const { user } = useAuth();
  const location = useLocation();

  // Không hiển thị sidebar nếu chưa đăng nhập
  if (!user) return null;

  const menuItems = user.role === "teacher"
    ? [
        {
          key: "/teacher",
          icon: <TeamOutlined />,
          label: <Link to="/teacher">Trang giảng viên</Link>,
        },
        {
          key: "/manage-courses",
          icon: <SettingOutlined />,
          label: <Link to="/manage-courses">Quản lý môn học</Link>,
        },
        {
          key: "/upload-material",
          icon: <UploadOutlined />,
          label: <Link to="/upload-material">Tải lên tài liệu</Link>,
        },
      ]
    : [
        {
          key: "/student",
          icon: <HomeOutlined />,
          label: <Link to="/student">Trang chủ</Link>,
        },
        {
          key: "/courses",
          icon: <BookOutlined />,
          label: <Link to="/courses">Môn học</Link>,
        },
        {
          key: "/documents",
          icon: <FileTextOutlined />,
          label: <Link to="/documents">Tài liệu</Link>,
        },
      ];

  return (
    <Sider
      width={220}
      style={{
        background: "#fff",
        minHeight: "100vh",
        borderRight: "1px solid #eee",
      }}
    >
      <div
        style={{
          padding: "16px",
          textAlign: "center",
          fontWeight: "bold",
          fontSize: "16px",
          borderBottom: "1px solid #eee",
        }}
      >
        Quản lý lớp học
      </div>

      <Menu
        mode="inline"
        selectedKeys={[location.pathname]}
        items={menuItems}
      />
    </Sider>
  );
};

export default Sidebar;
