import React, { useEffect, useState } from "react";
import { PageHeader, List, Card, Tag, message } from "antd";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const API = "http://localhost:3001";

export default function DocumentList() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [materials, setMaterials] = useState([]);

  useEffect(() => {
    if (!user || user.role !== "student") navigate("/login");
    else loadData();
  }, [user, navigate]);

  const loadData = async () => {
    try {
      const res = await axios.get(`${API}/materials`);
      setMaterials(res.data);
    } catch {
      message.error("Không thể tải tài liệu");
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: "0 auto", padding: 24 }}>
      <PageHeader title="Tài liệu môn học" />
      <List
        grid={{ gutter: 16, column: 1 }}
        dataSource={materials}
        renderItem={item => (
          <List.Item>
            <Card title={item.title}>
              <p>{item.description}</p>
              <Tag color="blue">
                <a href={item.link} target="_blank" rel="noreferrer">Xem tài liệu</a>
              </Tag>
            </Card>
          </List.Item>
        )}
      />
    </div>
  );
}
