import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useForm } from "react-hook-form";

const API = "http://localhost:3001";

export default function UploadMaterial() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [materials, setMaterials] = useState([]);
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
  } = useForm();

  useEffect(() => {
    if (!user || user.role !== "teacher") {
      navigate("/login");
    } else {
      fetchMaterials();
    }
  }, [user, navigate]);

  const fetchMaterials = async () => {
    try {
      const res = await axios.get(`${API}/materials`);
      setMaterials(res.data);
    } catch (error) {
      console.error("Lỗi khi tải danh sách tài liệu:", error);
    }
  };

  const onSubmit = async (data) => {
    if (!data.title || !data.description || !data.link) {
      alert("Vui lòng nhập đầy đủ thông tin");
      return;
    }

    const nextId = materials.length > 0
      ? Math.max(...materials.map((m) => Number(m.id))) + 1
      : 1;

    const newMaterial = {
      id: nextId,
      ...data,
    };

    try {
      const res = await axios.post(`${API}/materials`, newMaterial);
      setMaterials((prev) => [...prev, res.data]);
      reset();
    } catch (error) {
      console.error("Lỗi khi tải lên tài liệu:", error);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Bạn có chắc muốn xoá tài liệu này?")) return;
    try {
      await axios.delete(`${API}/materials/${id}`);
      setMaterials((prev) => prev.filter((m) => m.id !== id));
    } catch (error) {
      console.error("Lỗi khi xoá tài liệu:", error);
      alert("Xoá thất bại!");
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-4">Tải tài liệu lên</h2>
      <form onSubmit={handleSubmit(onSubmit)} className="bg-white p-4 rounded shadow space-y-4 mb-6">
        <div>
          <input
            {...register("title", { required: "Tiêu đề không được để trống" })}
            placeholder="Tiêu đề"
            className="w-full border p-2 rounded"
          />
          {errors.title && <p className="text-red-600">{errors.title.message}</p>}
        </div>
        <div>
          <textarea
            {...register("description", { required: "Mô tả không được để trống" })}
            placeholder="Mô tả"
            className="w-full border p-2 rounded"
          />
          {errors.description && <p className="text-red-600">{errors.description.message}</p>}
        </div>
        <div>
          <input
            {...register("link", {
              required: "Link không được để trống",
              pattern: {
                value: /^https?:\/\/.+/,
                message: "Link phải bắt đầu bằng http:// hoặc https://",
              },
            })}
            placeholder="Link tài liệu (PDF, video...)"
            className="w-full border p-2 rounded"
          />
          {errors.link && <p className="text-red-600">{errors.link.message}</p>}
        </div>
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Tải lên
        </button>
      </form>

      <h3 className="text-xl font-semibold mb-2">Danh sách tài liệu</h3>
      <ul className="space-y-4">
        {materials.map((doc) => (
          <li
            key={doc.id}
            className="bg-white p-4 rounded shadow flex justify-between items-start"
          >
            <div>
              <h4 className="font-semibold">{doc.title}</h4>
              <p className="text-gray-600">{doc.description}</p>
              <a
                href={doc.link}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 hover:underline"
              >
                Xem tài liệu
              </a>
            </div>
            <button
              onClick={() => handleDelete(doc.id)}
              className="bg-red-500 text-white px-3 py-1 rounded self-start"
            >
              Xoá
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
