import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  Typography,
  Form,
  Input,
  Button,
  Card,
  List,
  Popconfirm,
  message,
  Space,
} from "antd";

const { Title } = Typography;
const API = "http://localhost:3001";

export default function CourseManage() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form] = Form.useForm();

  useEffect(() => {
    if (!user || user.role !== "teacher") {
      navigate("/login");
    } else {
      fetchCourses();
    }
  }, [user, navigate]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API}/courses`);
      setCourses(res.data);
      setFilteredCourses(res.data);
    } catch (err) {
      message.error("Không thể tải danh sách môn học");
    } finally {
      setLoading(false);
    }
  };

  const onFinish = async (values) => {
    const isDuplicate = courses.some(
      (c) => c.code.toLowerCase() === values.code.toLowerCase()
    );

    if (isDuplicate) {
      message.warning("Mã môn học đã tồn tại");
      return;
    }

    const newCourse = { ...values, id: Date.now() };
    try {
      await axios.post(`${API}/courses`, newCourse);
      const updated = [...courses, newCourse];
      setCourses(updated);
      setFilteredCourses(updated);
      form.resetFields();
      message.success("Thêm môn học thành công");
    } catch (err) {
      message.error("Thêm môn học thất bại");
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API}/courses/${id}`);
      const updated = courses.filter((c) => c.id !== id);
      setCourses(updated);
      setFilteredCourses(updated);
      message.success("Xoá môn học thành công");
    } catch (err) {
      message.error("Xoá thất bại");
    }
  };

  const handleSearch = (e) => {
    const value = e.target.value.toLowerCase();
    const filtered = courses.filter(
      (course) =>
        course.name.toLowerCase().includes(value) ||
        course.code.toLowerCase().includes(value)
    );
    setFilteredCourses(filtered);
  };

  return (
    <div style={{ maxWidth: 900, margin: "0 auto", padding: 24 }}>
      <Title level={2}>Quản lý môn học</Title>

      <Card title="Thêm môn học mới" style={{ marginBottom: 24 }}>
        <Form form={form} layout="vertical" onFinish={onFinish}>
          <Form.Item
            name="code"
            label="Mã môn học"
            rules={[{ required: true, message: "Vui lòng nhập mã môn học" }]}
          >
            <Input placeholder="VD: IT001" />
          </Form.Item>
          <Form.Item
            name="name"
            label="Tên môn học"
            rules={[{ required: true, message: "Vui lòng nhập tên môn học" }]}
          >
            <Input placeholder="VD: Nhập môn lập trình" />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit">
              Thêm môn
            </Button>
          </Form.Item>
        </Form>
      </Card>

      <Card
        title="Danh sách môn học"
        extra={
          <Input.Search
            placeholder="Tìm theo mã hoặc tên"
            onChange={handleSearch}
            style={{ width: 300 }}
            allowClear
          />
        }
        loading={loading}
      >
        <List
          dataSource={filteredCourses}
          bordered
          renderItem={(course) => (
            <List.Item
              actions={[
                <Popconfirm
                  title="Xác nhận xoá môn học?"
                  onConfirm={() => handleDelete(course.id)}
                  okText="Xoá"
                  cancelText="Huỷ"
                >
                  <Button danger>Xoá</Button>
                </Popconfirm>,
              ]}
            >
              <Space direction="vertical">
                <strong>{course.code}</strong>
                <span>{course.name}</span>
              </Space>
            </List.Item>
          )}
        />
      </Card>
    </div>
  );
}
